mapper.select:
SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS FROM
(SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,ROW_NUMBER() OVER(ORDER BY MSNO DESC) MEMBER_SEEKER_SEQ FROM MEMBER_SEEKER where msmaster = 'seeker')
WHERE MEMBER_SEEKER_SEQ BETWEEN #{param1 } AND #{param2 }

SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS FROM
(SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,ROW_NUMBER() OVER(ORDER BY MSNO ASC) MEMBER_SEEKER_SEQ FROM MEMBER_SEEKER where msmaster = 'seeker')
WHERE MEMBER_SEEKER_SEQ BETWEEN #{param1 } AND #{param2 }

SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,MOLISENSE FROM
(SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,MOLISENSE,ROW_NUMBER() OVER(ORDER BY MSNO ASC) MEMBER_SEEKER_SEQ FROM MEMBER_SEEKER where msmaster = 'offer')
WHERE MEMBER_SEEKER_SEQ BETWEEN #{param1 } AND #{param2 }

SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,MOLISENSE FROM
(SELECT MSNO,MSID,MSPW,MSNAME,MSEMAIL,MSEMAILCHECKED,MSADDR,MSTEL,MSBIRTH,MSMASTER,MSPOSTCODE,MSDETAILADDRESS,MSEXTRAADDRESS,MOLISENSE, ROW_NUMBER() OVER(ORDER BY MSNO ASC) MEMBER_SEEKER_SEQ FROM MEMBER_SEEKER where msmaster = 'offer')
WHERE MEMBER_SEEKER_SEQ BETWEEN #{param1 } AND #{param2 }
